BEGIN
	SET NOCOUNT ON;

	DELETE FROM [AdminDB ].dbo.Applications WHERE ApplicationId = @ApplicationId;

END

BEGIN
	SET NOCOUNT ON;

	DELETE FROM [AdminDB ].dbo.Authorizations WHERE AuthorizationId = @AuthorizationId;

END

BEGIN SET NOCOUNT ON;
DELETE FROM [AdminDB ].dbo.RoleAuthorizations WHERE RoleId = @RoleId and AuthorizationId = @AuthorizationId
END

BEGIN
	SET NOCOUNT ON;

	DELETE FROM [AdminDB ].dbo.Roles WHERE RoleId = @RoleId;

END

BEGIN
	SET NOCOUNT ON;

	DELETE FROM [AdminDB ].dbo.UserApplications 
	WHERE UserId = @UserId and ApplicationId = @ApplicationId and RoleId = @RoleId;

END

