-- =============================================
CREATE PROCEDURE crud_InsertUserApplications
		@UserId [int],
		@ApplicationId [int],
		@RoleId [int]

AS

BEGIN
	SET NOCOUNT ON;

	INSERT INTO Users
	(
		Username, Email, PasswordHash, IsActive, IsRegistered, UserCreated, DateCreated, UserModified, DateModified
	)
	VALUES
	(
		@Username,
		@Email,
		@PasswordHash,
		@IsActive,
		@IsRegistered,
		@UserCreated,
		@DateCreated,
		@UserModified,
		@DateModified
	)

END

-- =============================================
-- Author:		<Author,,Name>
-- Create date: <Create Date,,>
-- Description:	<Description,,>
-- =============================================
CREATE PROCEDURE crud_InsertUsers
		@Username	    [varchar](100),
		@Email	        [varchar](255),
		@PasswordHash	[varchar](200),
		@IsActive	    [bit],
		@IsRegistered	[bit],
		@UserCreated	[varchar](20),
		@DateCreated	[datetime],
		@UserModified	[varchar](20),
		@DateModified	[datetime]

AS

BEGIN SET NOCOUNT ON;
Select ApplicationTitle, UserCreated, DateCreated, UserModified, DateModified From [AdminDB ].dbo.Applications
END

BEGIN SET NOCOUNT ON;
Select AuthorizationTitle, UserCreated, DateCreated, UserModified, DateModified From [AdminDB ].dbo.Authorizations
END

BEGIN SET NOCOUNT ON;
Select RoleTitle, UserCreated, DateCreated, UserModified, DateModified From [AdminDB ].dbo.Roles
END

BEGIN SET NOCOUNT ON;
Select Username, Email, PasswordHash, IsActive, IsRegistered, UserCreated, DateCreated, UserModified, DateModified From [AdminDB ].dbo.Users
END

BEGIN SET NOCOUNT ON;
Update Applications SET ApplicationTitle = @ApplicationTitle, UserCreated = @UserCreated, DateCreated = @DateCreated, UserModified = @UserModified, DateModified = @DateModified
END

